import React, { Component } from 'react';
import './App.css';
import GoogleMapReact from 'google-map-react';




const AnyReactComponent = ({ text }) => <div>{text}</div>;

class SimpleMap extends Component {

    componentDidMount() {




        var polys = [];
             var labels = [];


             // === A method for testing if a point is inside a polygon
             // === Returns true if poly contains point
             // === Algorithm shamelessly stolen from http://alienryderflex.com/polygon/
             GPolygon.prototype.Contains = function(point) {
               var j=0;
               var oddNodes = false;
               var x = point.lng();
               var y = point.lat();
               for (var i=0; i < this.getVertexCount(); i++) {
                 j++;
                 if (j == this.getVertexCount()) {j = 0;}
                 if (((this.getVertex(i).lat() < y) && (this.getVertex(j).lat() >= y))
                 || ((this.getVertex(j).lat() < y) && (this.getVertex(i).lat() >= y))) {
                   if ( this.getVertex(i).lng() + (y - this.getVertex(i).lat())
                   /  (this.getVertex(j).lat()-this.getVertex(i).lat())
                   *  (this.getVertex(j).lng() - this.getVertex(i).lng())<x ) {
                     oddNodes = !oddNodes
                   }
                 }
               }
               return oddNodes;
             }



             // Display the map, with some controls and set the initial location
             var map = new GMap2(document.getElementById("map"));
             map.addControl(new GLargeMapControl());
             map.addControl(new GMapTypeControl());
             map.setCenter(new GLatLng(42.16,-100.72),4);


             GEvent.addListener(map, "click", function(overlay,point) {
               if (!overlay) {
                 for (var i=0; i<polys.length; i++) {
                   if (polys[i].Contains(point)) {
                     map.openInfoWindowHtml(point,"You clicked on "+labels[i]);
                     //i = 999; // Jump out of loop
                   }
                 }
               }
             });


             // Read the data from states.xml

             var request = GXmlHttp.create();
             request.open("GET", "states.xml", true);
             request.onreadystatechange = function() {
               if (request.readyState == 4) {
                 var xmlDoc = GXml.parse(request.responseText);

                 // ========= Now process the polylines ===========
                 var states = xmlDoc.documentElement.getElementsByTagName("state");

                 // read each line
                 for (var a = 0; a < states.length; a++) {
                   // get any state attributes
                   var label  = states[a].getAttribute("name");
                   var colour = states[a].getAttribute("colour");
                   // read each point on that line
                   var points = states[a].getElementsByTagName("point");
                   var pts = [];
                   for (var i = 0; i < points.length; i++) {
                      pts[i] = new GLatLng(parseFloat(points[i].getAttribute("lat")),
                                          parseFloat(points[i].getAttribute("lng")));
                   }
                   var poly = new GPolygon(pts,"#000000",1,1,colour,0.5,{clickable:false});
                   polys.push(poly);
                   labels.push(label);
                   map.addOverlay(poly);
                 }
                 // ================================================
               }
           }






    }

  static defaultProps = {
    center: {
      lat: 59.95,
      lng: 30.33
    },
    zoom: 11
  };

  render() {
    return (
      // Important! Always set the container height explicitly
      <div style={{ height: '100vh', width: '100%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key: 'AIzaSyAVQ-sy1G7aUQWxuTZjNsW0I6IBzcNocmQ' }}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
        />
      </div>
    );
  }
}




class Question extends Component {

    constructor(props) {
        super(props);

        // Default all answers to false
        let answers = [];
        for (let i = 0; i < this.props.options.length; i++) {
            answers.push(false);
        }

        // Set initial state
        this.state = {
            answers: answers
        }
    }


    render() {

        const options = this.props.options.map((option, index) =>
            <label
                key={index}
                className="CheckmarkLabel"
            > {option}
                <input
                    type={this.props.type}
                    name={this.props.id}
                    onChange={(e) => {
                        // Update answers state
                        let answers = [...this.state.answers];
                        answers[index] = e.target.checked;
                        this.setState({answers: answers}, () => {
                            this.props.onAnswerChange(this.props.id, this.state.answers);
                        });
                    }}
                ></input>
                <span className={"Checkmark type-" + this.props.type}></span>
            </label>
        );

        return (
            <div className="QuestionContainer">
                <h1>{(this.props.id + 1) + '. ' +this.props.question}</h1>
                <div className="CheckboxContainer">
                    {options}
                </div>
            </div>
        );
    }
}



class Questions extends Component {

    constructor(props) {
        super(props);

        this.state = {
            questions: [],
            answers: [],
        }

        this.handleAnswerChange = this.handleAnswerChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentWillMount() {
        fetch("http://localhost:5000/get-questions")
        .then(res => res.json())
        .then(
            (questions) => {
                let answers = [];
                for (let i = 0; i < questions.length; i++) {
                    const q = questions[i];
                    let arr = [];
                    for (let i = 0; i < q.options.length; i++) {
                        arr.push(false);
                    }
                    answers.push(arr);
                }
                console.log(questions);
                this.setState({
                    questions: questions,
                    answers: answers,
                });
            }
        );
    }

    // Handle answer change by updating the state variable
    handleAnswerChange(id, answers) {
        let a = [...this.state.answers];
        a[id] = answers;
        this.setState({
            answers: a,
        });
    }

    // Handle submit of all answers
    handleSubmit() {
        // Send POST request to submit-answers route, which returns a guess
        fetch("http://localhost:5000/submit-answers",
        {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            method: "POST",
            body: JSON.stringify(this.state.answers)
        })
        .then(res => res.json())
        .then(
            (guess) => {
                alert('Guess: ' + guess.state);
            }
        );
    }

    render() {

        const questionEls = this.state.questions.map((question, index) =>
            <Question
                id={index}
                key={index}
                question={question.question}
                options={question.options}
                type={question.type}
                onAnswerChange={(id, answers) => {
                    this.handleAnswerChange(id, answers);
                }}
            />
        );

        return (
            <div className="ContentWrapper">
                <div className="ContentContainer">
                    {questionEls}
                    <button
                        onClick={() => {
                            this.handleSubmit();
                        }}
                        className="SubmitButton">
                        Submit
                    </button>
                </div>
            </div>
        );
    }
}

class App extends Component {

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                {/*<Questions />*/}
                <SimpleMap />
            </div>
        );
    }
}


export default App;
