import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper, Polygon} from 'google-maps-react';
import Borders from './StateBorders.js';

export class MapContainer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            guess: this.props.guess
        }
    }

    updateGuess(guess) {
        this.setState({guess: guess});
    }

    render() {
        console.log('RENDER', this.props.guess);
        let polygons = [];

        Object.keys(Borders).forEach(function(key){
            let weight = (typeof this.props.guess === 'undefined' ? 0 : this.props.guess[key]);
            console.log('weight:', weight);
            let coords = Borders[key].Coordinates;
            console.log('state:', key);
            let color =
            polygons.push(
                <Polygon
                    key={key}
                    paths={coords}
                    strokeColor="#fff"
                    strokeOpacity={0.8}
                    strokeWeight={1}
                    fillColor={'hsl(0, 100%, 50%)'}
                    fillOpacity={0.35}
                    onClick={() => {
                        console.log('click', key);
                    }}
                />
            )
        }.bind(this));

        return(
            <Map
                google={this.props.google}
                style={{width: '100%', height: '100%', position: 'relative'}}
                className={'map'}
                zoom={3}
            >
                {polygons}
            </Map>
        )
    }
}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyAVQ-sy1G7aUQWxuTZjNsW0I6IBzcNocmQ'
})(MapContainer)
