import React, { Component } from 'react';
import './App.css';

class Question extends Component {

    constructor(props) {
        super(props);

        // Default all answers to false
        let answers = [];
        for (let i = 0; i < this.props.options.length; i++) {
            answers.push(false);
        }

        // Set initial state
        this.state = {
            answers: answers
        }
    }


    render() {

        const options = this.props.options.map((option, index) =>
            <label
                class="CheckmarkLabel"
            > {option}
                <input
                    type="checkbox"
                    onChange={() => {
                        console.log('CHANGED OPTION ' + option);
                    }}
                ></input>
                <span class="Checkmark"></span>
            </label>
        );

        return (
            <div className="QuestionContainer">
                <h1>{this.props.question}</h1>
                <div class="CheckboxContainer">
                    {options}
                </div>
            </div>
        );
    }
}

class App extends Component {
  render() {
    return (
      <div className="ContentWrapper">
        <div className="ContentContainer">

            <Question
                question="Which childhood toys do you remember playing with?"
                options={["Hess Truck", "Slinky", "Etch A Sketch"]}
            />

            <Question
                question="Which of these fast food joints have you been to?"
                options={["Sonic", "McDonald's", "Bojangles'", "Burger King", "Chipotle"]}
            />

        </div>
      </div>
    );
  }
}


export default App;
